mapper.insert:

	SELECT IFNULL(MAX(board_num), 0) FROM board

INSERT
	INTO board
	VALUES (
		#{board_num} + 1
		, #{board_name}
		, #{board_subject}
		, #{board_content}
		, #{board_file}
		, #{board_file_path}
		, #{board_num} + 1
		, 0
		, 0
		, 0
		, now()
	)

mapper.select:
SELECT board_name
	FROM board
	WHERE
		board_num = #{board_num}
		AND board_name = #{id}

mapper.update:
UPDATE board
	SET
		board_subject = #{board_subject}
		, board_content = #{board_content}

			, board_file = #{board_file}
			, board_file_path = #{board_file_path}

	WHERE
		board_num = #{board_num}

UPDATE board
	SET
		board_re_seq = board_re_seq + 1
	WHERE
		board_re_ref = #{board_re_ref}
		AND board_re_seq > #{board_re_seq}

