mapper.insert:
	INSERT INTO auction_enroll 
				VALUES(
					  ((SELECT *
  							FROM 
  								(SELECT IFNULL(MAX(CAST(enroll_code AS UNSIGNED)) ,0) FROM auction_enroll) A)+1)
					, #{auction_code}
					, #{id}
					, 'N'
				)

mapper.select:
SELECT * 
	FROM auction_enroll
		WHERE auction_code = #{auction_code} AND member_id = #{id}

mapper.insert:
INSERT 
	INTO auction_log 
	VALUES (
		(SELECT *
			FROM (SELECT IFNULL(MAX(CAST(log_code  AS UNSIGNED)),0) +1
	 			FROM auction_log) A)
			, #{auctionCode}
			, #{id}
			, #{messages}
			, now()
			)
