mapper.insert:
INSERT
	INTO 
		account(
			id
			, user_seq_no
			, access_token
			, refresh_token
		)
	VALUES (
		#{id}
		, #{token.user_seq_no}
		, #{token.access_token}
		, #{token.refresh_token}
	);

mapper.update:
UPDATE	
	account
SET
	fintech_use_num = #{fintech_use_num}
WHERE
	id = #{member_id}

mapper.insert:

    		SELECT
    			COALESCE(MAX(trade_balance), 0) + #{trade_amount} AS trade_balance
    		FROM account_history

		INSERT
			INTO 
				account_history(
					trade_code
					, trade_member_id
					, trade_amount
					, trade_type
					, trade_balance
					, trade_date
				)
			VALUES (
				 #{trade_code}
				, #{id}
				, #{trade_amount}
				, #{trade_type}
				, #{trade_balance}
				, #{trade_date}
			)

mapper.update:
UPDATE member
	SET member_point = (
		 SELECT t.member_point + #{trade_amount}
			  FROM (
			    SELECT member_point
			    FROM member
			    WHERE member_id = #{id}
			  ) AS t
	)
	WHERE member_id = #{id}
