mapper.insert:
INSERT
	INTO 
		account(
			id
			, user_seq_no
			, access_token
			, refresh_token
		)
	VALUES (
		#{id}
		, #{token.user_seq_no}
		, #{token.access_token}
		, #{token.refresh_token}
	);

mapper.update:
