mapper.insert:

    SELECT CONCAT(IFNULL(MAX(CAST(comm_code AS UNSIGNED)), 0) + 1, '') FROM community

		INSERT 
			INTO community
			VALUES (
				#{comm_code}
				,#{id}
				,#{comm_title}
				,#{comm_content}
				,now()
				,"0"			
			)

mapper.update:
UPDATE community
SET comm_count = comm_count + 1
WHERE comm_code = #{comm_code}
