mapper.select:
SELECT count(*)
	FROM item
		WHERE member_id = #{openentId}

mapper.insert:

	SELECT IFNULL(MAX(market_code), 0) FROM market_paid

INSERT 
	INTO market_paid (
		market_code
		, member_id
		, item_code
		, buy_id, sell_id
		, market_price
		, market_pay_type
		, market_date
	)
	VALUES(
	#{market_code} + 1
	, #{item_detail.member_id}
	, #{item_detail.item_code}
	, #{oppenentId}
	, #{item_detail.member_id}
	, #{item_detail.item_price}
	, '직접거래'
	, now()
	)

mapper.delete:
DELETE 
	FROM market_paid
		WHERE 1=1
			AND item_code=#{item_detail.item_code}
			AND buy_id=#{oppenentId};

mapper.select:
SELECT item_code
	FROM market_chat_rooms
		WHERE room_code=#{item_code}

mapper.update:
UPDATE item
	SET
		item_status = #{item_status}
	 WHERE
	 	item_code=#{item_code}

mapper.insert:
INSERT INTO
	review
VALUES	(
	 REPLACE(#{item_code}, 'market', 'review')
	, #{item_code}
	, #{target_id}
	, #{reviewer_id}
	, #{review_content}
	, now()
	, #{rating}
	)

mapper.update:
UPDATE
	review 
SET 
	, review_content=#{review_content}
	, rating = #{rating}
WHERE
	1 = 1
	AND reviewer_id = #{reviewer_id}
	AND	review_item_code = #{item_code}
