mapper.select:
SELECT *
    FROM market_chat_rooms
       WHERE item_code=#{item_code};

SELECT count(*)
   FROM item
      WHERE member_id = #{openentId}

SELECT myChatRecentList.room_code,is_exist_status, chat_code, chat_content, member_nickname,member_image,chat_time,myChatRecentList.item_subject,chat_openDate,item_status,item_code FROM ( SELECT room_code,is_exist_status, chat_code, chat_content, member_nickname,member_image, ROW_NUMBER() OVER (PARTITION BY room_code ORDER BY chat_code DESC) AS row_num , chat_time, a.item_subject ,chat_openDate,item_status, item_code FROM ( SELECT market_chat_rooms.room_code, market_chat_rooms.is_exist_status , chat_code, market_chat_rooms.chat_content, member.member_nickname, member.member_image, chat_time, item.item_subject , market_chat_rooms.chat_openDate, item.item_status , item.item_code FROM market_chat_messages JOIN member ON member.member_id = market_chat_messages.sell_member_id JOIN market_chat_rooms ON market_chat_rooms.room_code = market_chat_messages.room_code JOIN item ON item.item_code=market_chat_rooms.item_code WHERE buy_member_id = #{id}
UNION SELECT market_chat_rooms.room_code, market_chat_rooms.is_exist_status , chat_code, market_chat_rooms.chat_content, member.member_nickname, member.member_image, chat_time, item.item_subject, market_chat_rooms.chat_openDate, item.item_status , item.item_code FROM market_chat_messages JOIN member ON member.member_id = market_chat_messages.buy_member_id JOIN market_chat_rooms ON market_chat_rooms.room_code = market_chat_messages.room_code JOIN item ON item.item_code=market_chat_rooms.item_code WHERE sell_member_id = #{id}
) a ) AS myChatRecentList WHERE 1=1 AND row_num = 1 AND is_exist_status = 'N' ORDER BY chat_time desc


SELECT *
   FROM
      market_chat_messages
   LEFT JOIN market_chat_rooms ON
      market_chat_rooms.room_code = market_chat_messages.room_code
   WHERE
      sell_member_id = #{id}
      OR buy_member_id =  #{id}
      AND market_chat_rooms.room_code != #{room_code}

SELECT *
    FROM item
       WHERE item_code = #{item_code}

