mapper.update:
		 UPDATE member
      SET
         member_point = member_point - #{itemPrice}
       WHERE
          member_id=#{id}

		 UPDATE member
      SET
         member_point = member_point + #{itemPrice}
       WHERE
          member_id=#{sellId}

mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY CAST(r.report_code AS UNSIGNED) DESC ) AS rownum ,i.* , count(*) over() AS totalCnt , r.report_targetId , r.member_id AS repId , r.report_content , rc.rep_cnt FROM item i LEFT JOIN report r ON i.member_id = r.report_targetId AND i.item_code = r.item_code LEFT JOIN (SELECT item_code, count(item_code) AS rep_cnt FROM report GROUP BY item_code) AS rc ON i.item_code = rc.item_code WHERE 1=1 AND r.report_code IS NOT null
AND (item_subject LIKE '%${searchKeyword}%' OR item_content LIKE '%${searchKeyword}%')
AND item_status = #{typeSelect}
LIMIT ${startNum}, ${endNum}

SELECT * , ROW_NUMBER() OVER(ORDER BY CAST(report_code AS UNSIGNED) DESC) AS rownum , count(*) over() AS totalCnt FROM report WHERE 1=1
AND (report_targetId LIKE '%${searchKeyword}%' OR member_id LIKE '%${searchKeyword}%')
AND report_type = #{typeSelect}
LIMIT ${startNum}, ${endNum}
