mapper.insert:
INSERT INTO member
	VALUES(
		   #{member_id}
		   , #{member_name}
		   , #{member_nickname}
		   , #{member_pw}
		   , #{member_address}
		   , #{member_address_detail}
		   , #{member_bday}
		   , #{member_email}
		   , #{member_tel}
		   , '0'
		   , 'N' -- member_auth_status
		   , now()
		   , #{member_image}	-- member_image
		   , '1' -- gradeScore 
		   , '0' -- declare_count
		   , POINT(${member_location})
		   , 'N' -- member_delete_status
	)

mapper.select:
SELECT member_id
 FROM member
	WHERE member_id = #{member_id}

mapper.update:
UPDATE member
	SET member_pw = #{member_pw}
		WHERE member_id = #{member_id}

UPDATE member
SET
member_nickname = #{member_nickname}
,member_pw = #{member_pw2}
,member_address = #{member_address}
,member_address_detail = #{member_address_detail} 
,member_bday = #{member_bday}
,member_tel = #{member_tel}
,member_image = #{member_image}
,member_location = POINT(${member_location})
WHERE member_id = #{member_id}

mapper.select:
SELECT *
	FROM member
		WHERE 
			member_id = #{member_id}
			AND member_name = #{member_name}
			AND member_bday = #{member_bday}
			AND member_tel = #{member_tel}

SELECT 	*
	FROM wish
		WHERE member_id = #{member_id}
		AND	wish_item_code = #{wish_item_code}

mapper.delete:
DELETE FROM 
		wish
WHERE 
		member_id = #{member_id}
		AND	wish_item_code = #{wish_item_code}

mapper.update:
UPDATE member
	SET member_auth_status = 'Y'
	WHERE member_id = #{member_id}

mapper.select:
SELECT * FROM member
	WHERE member_name = #{member_name}
		AND member_bday = #{member_bday}
		AND member_tel = #{member_tel}

mapper.update:
UPDATE 
	member
SET
	member_point = #{point}
WHERE
	member_id = #{id}

mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY member_bday DESC ) AS rownum , m.* , count(*) over() AS totalCnt , ifnull(rc.rep_cnt, 0) AS rep_cnt FROM member m LEFT JOIN (SELECT report_targetId, count(report_targetId) AS rep_cnt FROM report GROUP BY report_targetId) AS rc ON m.member_id = rc.report_targetId WHERE 1=1
AND (member_id LIKE '%${searchKeyword}%' OR member_nickname LIKE '%${searchKeyword}%')
AND member_auth_status = #{typeSelect}
LIMIT ${startNum}, ${endNum}

mapper.update:
UPDATE member
	SET 
		member_point = CAST(member_point AS UNSIGNED) - #{pay_price}
	WHERE 
		member_id = #{id}
