mapper.insert:
INSERT INTO member
	VALUES(
		   #{member_id}
		   , #{member_name}
		   , #{member_nickname}
		   , #{member_pw}
		   , #{member_address}
		   , #{member_address_detail}
		   , #{member_bday}
		   , #{member_email}
		   , #{member_tel}
		   , '0'
		   , 'N' -- member_auth_status
		   , now() 
		   , ''	-- member_image
		   , '0' -- grade_score
		   , '0' -- declare_count
		   , #{member_X}
		   , #{member_Y}
	)

mapper.update:
UPDATE member
	SET member_auth_status = 'Y'
	WHERE member_id = #{member_id}
