mapper.insert:
INSERT INTO member
	VALUES(
		   #{member_id}
		   , #{member_name}
		   , #{member_nickname}
		   , #{member_pw}
		   , #{member_address}
		   , #{member_address_detail}
		   , #{member_bday}
		   , #{member_email}
		   , #{member_tel}
		   , '0'
		   , 'N' -- member_auth_status
		   , now() 
		   , ''	-- member_image
		   , '0' -- grade_score
		   , '0' -- declare_count
		   , #{member_X}
		   , #{member_Y}
	)

mapper.select:
SELECT member_id
 FROM member
	WHERE member_id = #{member_id}

mapper.update:
UPDATE member
	SET member_pw = #{member_pw}
		WHERE member_id = #{member_id}

mapper.select:
SELECT *
	FROM member
		WHERE 
			member_id = #{member_id}
			AND member_name = #{member_name}
			AND member_bday = #{member_bday}
			AND member_tel = #{member_tel}

mapper.update:
UPDATE member
	SET member_auth_status = 'Y'
	WHERE member_id = #{member_id}

mapper.select:
SELECT * FROM member
	WHERE member_name = #{member_name}
		AND member_bday = #{member_bday}
		AND member_tel = #{member_tel}
